import React from "react";
import { connect } from "react-redux";
import { compose } from "redux";
import ChatPage from "./ChatPage";
import { Route } from "react-router";
import { addSms } from "../../Redux/usersReducer";
class ChatContainer extends React.Component {
  render() {
    return (
      <Route
        path={"/chat/" + this.props.currentId}
        render={() => <ChatPage {...this.props} />}
      />
    );
  }
}
const mapStateToProps = (state) => {
  let currentUser = state.users.currentId - 1;
  return {
    currentName: state.users.currentName,
    currentId: state.users.currentId,
    userData: state.users.usersData[currentUser],
  };
};
export default compose(connect(mapStateToProps, { addSms }))(ChatContainer);
