import s from "./ChatPage.module.css";
import { reduxForm, Field } from "redux-form";
import { useState, useEffect } from "react";

const ChatPage = (props) => {
  let [newTxt, setNewText] = useState(props.userData);
  useEffect(() => {
    setNewText(props.userData);
  }, [props.userData]);

  const addSms = (value) => {
    props.addSms(value.chatText, props.currentId);
  };
  const userData = props.userData.messages;
  const message = userData.map((m) => {
    return (
      <div>
        <div>{m.message}</div>
        <div>{m.date}</div>
      </div>
    );
  });
  return (
    <div className={s.chatPageWrapper}>
      <div>{props.currentName}</div>
      <div>{message}</div>
      <div>{newTxt}</div>
      <div>
        <ChatReduxForm onSubmit={addSms} />
      </div>
    </div>
  );
};
const ChatForm = (props) => {
  return (
    <form onSubmit={props.handleSubmit}>
      <div>
        <Field
          component={"textarea"}
          name={"chatText"}
          placeholder="Type your message"
        />
      </div>
      <div>
        <button>send</button>
      </div>
    </form>
  );
};
const ChatReduxForm = reduxForm({ form: "chatForm" })(ChatForm);

export default ChatPage;
