import s from "./ChatPage.module.css";

const ChatPage = (props) => {
  let message = props.currentMessages.map((m) => {
    return (
      <div>
        <div>{m.message}</div>
        <div>{m.date}</div>
      </div>
    );
  });
  return (
    <div className={s.chatPageWrapper}>
      <div>{props.currentName}</div>
      <div>{message}</div>
      <div>
        <ChatReduxForm onSubmit={addSms} />
      </div>
    </div>
  );
};
const ChatForm = (props) => {
  return (
    <form onSubmit={props.handleSubmit}>
      <div>
        <Field
          validate={[required, setMaxlength15]}
          component={PostAreaStyle}
          name={"dialogText"}
          placeholder="Enter your message"
          Formtype="textarea"
        />
      </div>
      <div>
        <button>Send</button>
      </div>
    </form>
  );
};
const DialogReduxForm = reduxForm({ form: "dialogForm" })(DialogForm);

export default ChatPage;
