import { chatApi } from "../Api/Api";

const SET_CURRENT_CHAT = "SET_CURRENT_CHAT";
const ADD_SMS = "ADD_SMS";
let initialState = {
  usersData: [
    {
      id: 1,
      name: "Misha",
      lastMessage: "hello",
      date: "26/12/2018",
      photo: "",
      messages: [
        { message: "hello there", date: "26/12/2018", mine: false },
        { message: "how are u", date: "26/12/2018", mine: true },
      ],
    },
    {
      id: 2,
      name: "Vasia",
      lastMessage: "hello",
      date: "15/11/2019",
      photo: "",
      messages: [
        { message: "hello there", date: "15/11/2019" },
        { message: "how are u", date: "15/11/2020" },
      ],
    },
    {
      id: 3,
      name: "Kate",
      lastMessage: "hello",
      date: "4/10/2017",
      photo: "",
      messages: [
        { message: "hello there", date: "11/10/2017" },
        { message: "how are u", date: "15/10/2017" },
      ],
    },
    {
      id: 4,
      name: "Eliza",
      lastMessage: "hello",
      date: "16/11/2017",
      photo: "",
      messages: [
        { message: "hello there", date: "16/11/2017" },
        { message: "how are u", date: "22/01/2020" },
      ],
    },
    {
      id: 5,
      name: "Petro",
      lastMessage: "hello",
      date: "5/11/2017",
      photo: "",
      messages: [
        { message: "hello there", date: "5/11/2017" },
        { message: "hello there", date: "10/12/2017" },
      ],
    },
    {
      id: 6,
      name: "Stiopa",
      lastMessage: "hello",
      date: "15/01/2020",
      photo: "",
      messages: [
        { message: "hello there", date: "15/01/2020" },
        { message: "hello there", date: "19/01/2020" },
      ],
    },
    {
      id: 7,
      name: "Adrian",
      lastMessage: "hello",
      date: "18/02/2017",
      photo: "",
      messages: [
        { message: "hello there", date: "18/02/2017" },
        { message: "hello there", date: "19/03/2018" },
      ],
    },
    {
      id: 8,
      name: "Karl",
      lastMessage: "hello",
      date: "14/04/2013",
      photo: "",
      messages: [
        { message: "hello there", date: "14/04/2013" },
        { message: "hello there", date: "16/05/2014" },
      ],
    },
    {
      id: 9,
      name: "Oleg",
      lastMessage: "hello",
      date: "14/06/2019",
      photo: "",
      messages: [
        { message: "hello there", date: "14/06/2019" },
        { message: "hello there", date: "15/07/2020" },
      ],
    },
    {
      id: 10,
      name: "Stefania",
      lastMessage: "hello",
      date: "12/08/2017",
      photo: "",
      messages: [
        { message: "hello there", date: "12/08/2017" },
        { message: "hello there", date: "13/09/2017" },
      ],
    },
  ],
  currentName: "Vasia",
  currentMessages: "hello",
  currentId: 0,
};

const usersReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_CURRENT_CHAT: {
      return {
        ...state,
        currentName: action.name,
        currentMessages: action.messages,
        currentId: action.id,
      };
    }
    case ADD_SMS: {
      let id = action.id;
      const userInf = state.usersData.map((el) => {
        if (el.id === id) {
          el.date = action.userDate;
          el.lastMessage = action.message;
          el.myMessages = [
            ...el.myMessages,
            { message: action.message, date: action.date },
          ];
        }
        return el;
      });
      return {
        ...state,
        ...state.usersData,
        usersData: userInf,
      };
    }
    default:
      return state;
  }
};
export const setCurrentChat = (name, messages, id) => {
  return {
    type: SET_CURRENT_CHAT,
    name,
    messages,
    id,
  };
};
export const addSms = (message, id, date, userDate) => {
  debugger;

  return {
    type: ADD_SMS,
    message,
    id,
    date,
    userDate,
  };
};

export const getJokesThunk = (date, id, userDate) => {
  return (dispatch) => {
    chatApi.getJoke().then((response) => {
      dispatch(addSms(response.data.value, id, date, userDate));
    });
  };
};

export default usersReducer;
